        foreach ($jawaban as $index) {

            $id_soal = 1;
            $array_soal = 0;
            foreach ($index['soal'] as $index_soal) {
                $dataSoal = SoalTugas::where('id', $id_soal)->first();
                $analyzer = new CosineSimilarityController();
                $is_benar = null;

                if ($dataSoal->jawaban_benar == $index_soal) {
                    $analysis = $analyzer->compareSentences($dataSoal->alasan_jawaban, $index['alasan'][$array_soal]);
                    $similarity_percentage = $analysis['similarity_percentage'];
                    $is_benar = true;
                } else {
                    $similarity_percentage = 0;
                }
                // dd($analysis);
                JawabanTugas::create([
                    'user_id' => $index['id'],
                    'tugas_id' => 1,
                    'soal_tugas_id' => $id_soal,
                    'jawaban' => $index_soal,
                    'penjelasan_jawaban' => $index['alasan'][$array_soal],
                    'nilai_cosine_similarity' => $similarity_percentage,
                    'is_benar' => $is_benar,
                ]);
                $array_soal++;
                $id_soal++;
            }

            $array_essai = 0;
            foreach ($index['essai'] as $essai) {
                $dataSoal = SoalTugas::where('id', $id_soal)->first();
